package com.lao.constructors;

public class NoArgumentConstructor {
	//Example of a NoArgument Constructor

	public NoArgumentConstructor() {
		// TODO Auto-generated constructor stub
		int sNo= 02;
		String name="Muthu";
		System.out.println("No Argument Constructor is created with Serial no " +sNo+ " and Name is "+name);
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		NoArgumentConstructor example = new NoArgumentConstructor();
	}
		

}
