package com.lao.exception_handling;

public class CheckedExceptionExample {

	
	//Exception which are indicated during the compile time 
	// are known as Checked Exception or compile time exception //
	
	//Exception which are indicated during the run time 
	// are known as Unchecked Exception or Run time exception 
	
	static String name;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(name);
		System.out.println(name.length());
	}

}
